@import url("https://fonts.googleapis.com/css2?family=Lato:ital,wght@0,700;1,400&family=Merriweather:ital@1&family=Praise&display=swap");
@import "config";
@import "home";
@import "menu";
@import "about";
@import "projects";
@import "contact";
@import "responsive";
@import "certificates";

body {
    background: $primary-clr;
    color: set-text-color($primary-clr);
    height: 100vh;
    font-family: "Lato", sans-serif;
    font-family: "Merriweather", serif;
    line-height: 1;
    text-shadow: 1px rgb(169,169,169);
}

h1,
h2,
h3 {
    font-weight: 400;
}

a {
    color: set-text-color($primary-clr);
    text-decoration: none;
}

.text-secondary {
    color: $secondary-clr;
}

header {
    position: fixed;
    z-index: 2;
    width: 100%;
    padding: 1rem;
}

main {
    height: 100%;
    width: 100%;

    .social-icons {
        position: fixed;
        bottom: 1rem;
        left: 1rem;
        text-shadow: none;

        a {
            padding: 0.4rem;
            @include transition-ease;

            &:hover {
                color: $secondary-clr;
                transform: scale(1.3);
            }
        }
    }
}

footer {
    font-size: 1rem;
    position: fixed;
    bottom: 0.4rem;
    right: 1rem;
    text-align: right;
    padding: 1rem;
    color: set-text-color($primary-clr);
}

